A = (1,1);
B = (-1,1);
C = (-1,-1);
D = (1,-1);

draw((-1.5,0)--(1.5,0));
draw((0,-1.5)--(0,1.5));
draw(A--B--C--D--cycle);

label("$(1,1)$", A, NE);
label("$(-1,1)$", B, NW);
label("$(-1,-1)$", C, SW);
label("$(1,-1)$", D, SE);
[/asy]

